mapper.select:
SELECT user_id, user_name, nickname, avatar, ignore_anon
FROM users
WHERE user_id = #{user_id}

mapper.update:
UPDATE users

    user_name = #{param.user_name},
    nickname = #{param.nickname},
    avatar = #{param.avatar}

WHERE user_id = #{key.user_id}

UPDATE users
SET ignore_anon = true
WHERE user_id = #{user_id}

UPDATE users
SET ignore_anon = false
WHERE user_id = #{user_id}
